def read_fasta(filename):
    f = open(filename, "r")
    Lines = f.readlines()
    # print(f.read())
    seqs = [""]
    i = -1
    for line in Lines:
        if len(line) < 10:
            continue
        if ">" in line:
            i += 1
            seqs.append("")
            continue
        else:
            line = line.strip('\n')
            seqs[i] += line
    min_len = 999999999
    min_index = 0
    for j in range(0, len(seqs)):            
        if (len(seqs[j]) < min_len and len(seqs[j]) != 0): 
            min_len = len(seqs[j])
            min_index = j
    #     print(j, " ", len(seqs[j]),"\n", seqs[j], "\n")
    # print (len(seqs[min_index]))
    for j in range(0, len(seqs)):
        seqs[j] = seqs[j][0, len(seqs[min_index])] # make len of shortest
        print(j, " ", len(seqs[j]),"\n", seqs[j], "\n")
    return len(seqs[min_index])
    




